function start(){bindEventListeners(document.getElementsByClassName("board")[0].children)}function bindEventListeners(e){for(var t=0;t<e.length;t++)e[t].addEventListener("contextmenu",makeGreen),e[t].addEventListener("click",makeBlue)}function makeGreen(e){e.preventDefault(),e.target.classList.toggle("green"),updateCounts()}function makeBlue(e){e.preventDefault(),e.target.classList.toggle("blue"),updateCounts()}function updateCounts(){var e={blue:0,green:0,invisible:0};displayTotals(e)}function displayTotals(e){for(var t in e)document.getElementById(t+"-total").innerHTML=e[t]}document.addEventListener("DOMContentLoaded",start);